        public static Entry[] LoadEntries(string uosAuthorizationToken, string bucketUuid)
        {
            if (string.IsNullOrEmpty(bucketUuid))
            {
                return null;
            }
            string url = string.Format("{0}api/v1/buckets/{1}/entries/?page={2}&per_page={3}", UOSInfo.ApiHost, bucketUuid, 1, 100000);

            try
            {
                using (HttpWebResponse resp = (HttpWebResponse)Util.GetHttpWebRequest4UOSThreaded(uosAuthorizationToken, url, "GET").GetResponse())
                {
                    if (resp.StatusCode.Equals(HttpStatusCode.OK))
                    {
                        string strJson;
                        using (Stream stream = resp.GetResponseStream())
                        {
                            using (StreamReader reader = new StreamReader(stream))
                            {
                                strJson = reader.ReadToEnd();
                            }
                        }
                        return JsonUtility.FromJson<EntriesRoot>("{\"Entries\":" + strJson + "}").Entries;
                    }
                    else
                    {
                        Debug.LogError(string.Format("Failed loading entries, bucketUuid: {0}", bucketUuid));
                    }
                }
            }
            catch (Exception e)
            {
                EditorUtility.DisplayDialog("Load Entries Error", e.Message, "OK");
            }
            return null;
        }

        public static bool SyncEntries(string uosAppId, string uosAuthorizationToken, string bucketUuid, List<System.Tuple<ASResourceType, string>> toBeUploaded)
        {
            bool allSuccess = true;
            if (string.IsNullOrEmpty(bucketUuid))
                return allSuccess;

            var progressBarTitle = "AutoStreaming data uploading";

            EditorUtility.DisplayProgressBar(progressBarTitle, "Fetching remote entries ...", 0.1f);
            var remoteEntries = LoadEntries(uosAuthorizationToken, bucketUuid);

            var localEntries = GetLocalFileEntries(uosAppId, toBeUploaded);
            var toBeUpdated = new Dictionary<string, EntryInfo>();
            var toBeDeleted = new List<string>();

            int i = 0;
            float total = remoteEntries.Length;
            foreach (var entry in remoteEntries)
            {
                EditorUtility.DisplayProgressBar(progressBarTitle, "Syncing entries: " + (i++) + "/" + total, i / total);
                string entryPath = entry.path.StartsWith("/") ? entry.path.Substring(1) : entry.path;
                if (localEntries.ContainsKey(entryPath))
                {
                    EntryInfo info = localEntries[entryPath];
                    if (!entry.content_hash.Equals(info.content_hash))
                    {
                        toBeUpdated.Add(entry.entryid, info);
                    }
                    localEntries.Remove(entryPath);
                }
                else
                {
                    toBeDeleted.Add(entry.entryid);
                }
            }

            //delete entries
            i = 0;
            total = toBeDeleted.Count;
            foreach (var id in toBeDeleted)
            {
                EditorUtility.DisplayProgressBar(progressBarTitle, "Removing unused entries: " + (i++) + "/" + total, i / total);
                if (!DeleteEntry(uosAuthorizationToken, bucketUuid, id))
                {
                    Debug.LogError("Failed deleting UOS entry "+ id + ". Please try again after all upload finished.");
                    allSuccess = false;
                }
            }

            // update entries
            allSuccess = allSuccess && Util.UpdateFiles(uosAuthorizationToken, uosAppId, bucketUuid, toBeUpdated);

            //upload new entries
            allSuccess = allSuccess && Util.UploadFiles(uosAuthorizationToken, uosAppId, bucketUuid, localEntries);

            EditorUtility.ClearProgressBar();
            return allSuccess;
        }

        private static Dictionary<string, EntryInfo> GetLocalFileEntries(string uosAppId, List<System.Tuple<ASResourceType, string>> toBeUploaded)
        {
            var entriesInfo = new Dictionary<string, EntryInfo>();
            foreach (var pair in toBeUploaded)
            {
                switch (pair.Item1)
                {
                    case ASResourceType.StreamedResource:
                        GetStreamingFiles(uosAppId, entriesInfo, pair.Item2, new List<string> { ".abas" }, "AS/");
                        break;
                    case ASResourceType.CustomCloudAssets:
                        GetStreamingFiles(uosAppId, entriesInfo, pair.Item2, null, "CUS/",true); // accept all extensions, recursive
                        break;
                    case ASResourceType.AddressableAsset:
                        GetStreamingFiles(uosAppId, entriesInfo, pair.Item2, new List<string> { ".json", ".hash", ".bundle" });
                        break;
                    case ASResourceType.InstantGame:
                        GetStreamingFiles(uosAppId, entriesInfo, pair.Item2, new List<string> { ".zip", ".png", ".instantgame_meta" });
                        break;
                }
            }

            return entriesInfo;
        }

        private static void GetStreamingFiles(string uosAppId, Dictionary<string, EntryInfo> dict, string dir, List<string> extensions, string parent = null, bool recursive = false)
        {
            if (!Directory.Exists(dir))
                return;

            var root = new DirectoryInfo(dir);
            var files = root.GetFiles();
            foreach (var file in files)
            {
                // When @extension == null, we accept all extensions
                if (extensions != null && !extensions.Contains(file.Extension.ToLower()))
                    continue;

                var hash = Util.GetFileHash128(file.FullName).ToString();
                var path = parent + file.Name;
                #if UNITY_WEBGL && (IG_C301 || IG_C302 || IG_C303 || TUANJIE_2022_3_OR_NEWER)
                path = parent + Util.AppendHash128ToFileNameIfNeeded(file.FullName, hash);
                #endif
                //Debug.Log("File: "+ file.FullName + " Path: " + path);
                var entry = new EntryInfo(file.FullName, path, hash, file.Length,
                    MimeMapping.GetContentTypeFromExtension(file.FullName), uosAppId);

                dict.Add(entry.path, entry);
            }

            if (!recursive)
                return;

            var subDirs = root.GetDirectories();
            foreach (var d in subDirs)
            {
#if UNITY_WEBGL
                if (parent == "CUS/" && (d.Name == "StreamingAssets" || d.Name == "Assets" || d.Name == "Code"))
                {
                    //do not add "CUS/"
                    GetStreamingFiles(uosAppId, dict, d.FullName, extensions, d.Name + "/", recursive);
                }
                else
#endif
                GetStreamingFiles(uosAppId, dict, d.FullName, extensions, parent + d.Name +"/", recursive);
            }
        }

        internal static bool CreateEntry(string uosAuthorizationToken, string uosAppId, string bucketUuid, EntryInfo info)
        {
            string url = string.Format("{0}api/v1/buckets/{1}/entries/", UOSInfo.ApiHost, bucketUuid);
            string requestBody = JsonUtility.ToJson(new EntryParams(info));
            try
            {
                using (HttpWebResponse resp = (HttpWebResponse)Util.GetHttpWebRequest4UOSThreaded(uosAuthorizationToken, url, "POST", requestBody).GetResponse())
                {
                    if (resp.StatusCode.Equals(HttpStatusCode.OK))
                    {
                        return true;
                    }
                    else
                    {
                        Debug.LogError(string.Format("Failed creating entry, bucketUuid: {0}, Entry: {1}", bucketUuid, info.full_path));
                    }
                }
            }
            catch (Exception e)
            {
                Debug.LogError("Create Entries Error: " + info.path + e.Message);
            }
            return false;
        }

