        private static void CreateMultiEntries(string uosAuthorizationToken, string uosAppId, string bucketUuid, List<EntryInfo> createEntries)
        {
            if (createEntries.Count == 0)
                return;

            string url = string.Format("{0}api/v1/buckets/{1}/multientries/", UOSInfo.ApiHost, bucketUuid);
            string requestBody = "";

            foreach (var info in createEntries)
            {
                if (string.IsNullOrEmpty(requestBody))
                {
                    requestBody = "[\n";
                }
                else
                {
                    requestBody += ",";
                }
                requestBody += JsonUtility.ToJson(new EntryParams(info));
            }
            requestBody += "]\n";

            try
            {
                HttpWebRequest request = Util.GetHttpWebRequest4UOSThreaded(uosAuthorizationToken, url, "POST", requestBody);
                request.Timeout = createEntries.Count * 1000;
                using (HttpWebResponse resp = (HttpWebResponse)request.GetResponse())
                {
                    if (!resp.StatusCode.Equals(HttpStatusCode.OK))
                    {
                        Debug.LogError(string.Format("Failed creating multi entries, bucketUuid: {0}", bucketUuid));
                    }
                }
            }
            catch (Exception e)
            {
                Debug.LogError("Create Entries Error: "  + e.Message);
            }
        }
